		{
			if (base.DialogResult != DialogResult.OK)
			{
				CopyMemory(canvas.ImageData, Clone, canvas.Image.Height * Stride);
				canvas.Refresh();
			}
			if (Clone != null)
			{
				Marshal.FreeHGlobal((IntPtr)Clone);
			}
		}

