					for (int j = 0; j < num2; j++)
					{
						if (OptPureC.Checked)
						{
							IM_Arithmetic_Scale_Image_PureC(Clone, canvas.ImageData, canvas.Image.Width, canvas.Image.Height, canvas.Stride, Radius.Value, Add.Value);
